import React,{useCallback, useEffect, useState} from "react";
import { useHttp } from "../hooks/http.hook";
import { useNavigate } from "react-router-dom";


export const MainPageCart = ({data}) => {
    const link = (id) =>{return "/apartmen/"+id;}
    
    const [currentPage, setCurrentPage] = useState(1);
    const [perPage] = useState(8);

    const LastIndex = currentPage * perPage;
    const FirstIndex = LastIndex - perPage;
    let current = data.slice(FirstIndex,LastIndex);

    const paginate = pageNumber => setCurrentPage(pageNumber);

    return(
        <div>
            <nav>
                <div class="nav-wrapper red">
                    <a href = '/'><img class="brand-logo logou" src="https://cdn5.zp.ru/job/attaches/2016/07/99/b2/99b2dc5b6ce63ac1f1727bab91700532.png"/></a>
                </div>
            </nav>
            <div class="pad">
                <div class="row">
                    {current.map(apartmen => (
                        <div class="col s12 m6 l3">
                            <div class="row centre">
                                <div class="col s12 m7">
                                    <a href = {link(apartmen.i)}>
                                    <div class="card">
                                        <div class="card-image">
                                            <img class = 'dark'src={apartmen.layout_image}/>
                                            <span class="card-title">Квартира №{apartmen.id}</span>
                                        </div>
                                        <div class="card-content">
                                            <p>I am a very simple card. I am good at containing small bits of information.
                                            I am convenient because I require little markup to use effectively.</p>
                                        </div>
                                    </div>
                                    </a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            {<Pagination 
                data ={data}
                perPage = {perPage}
                total = {data.length}
                paginate={paginate}
            />}
        </div>
        
    )
}

export const Pagination = ({data, perPage, total, paginate}) => {
    const pageNumber = [];
    for(let i = 1; i <= Math.ceil(total/perPage); i++)
    {
        pageNumber.push(i);
    }

    return(
            <ul class="pagination flexrow">
            {pageNumber.map(number => (
                <li class="waves-effect" key ={number}><a href="#!" onClick={()=>paginate(number)}>{number}</a></li>
            ))}
            </ul>
    )
}

export const MainPage = () =>{
    const {request, loading} = useHttp();
    const [data, setData] = useState(null);
    const navigate =  useNavigate();

    const getDataAll = useCallback( async()=>{
        try
        {
            const fetched = await request('/api/all', 'GET', null,{});
            setData(fetched);
        }
        catch(e){}
    },[request]);

    useEffect(()=>{
        getDataAll();
    },[getDataAll]);

    return(
        <div>
            {!loading && data && <MainPageCart data={data}/>}
        </div>
    );
}
