import React,{useCallback, useEffect, useState} from "react";
import { useParams,useNavigate } from "react-router-dom";
import { useHttp } from "../hooks/http.hook";

export const ApartmenPageCart = ({apartment}) => {
    return(
        <div>
            <nav>
                <div class="nav-wrapper colormenu menu">
                    <a href = '/'><img class="brand-logo logou" src="https://static.tildacdn.com/tild3738-3866-4537-a335-363534343034/__white.png"/></a>
                </div>
            </nav>
            <div id="container">	
	<div class="product-details">
	<h1>Biru Putaran</h1>
		<p class="information">" Especially good for container gardening, the Angelonia will keep blooming all summer even if old flowers are removed. Once tall enough to cut, bring them inside and you'll notice a light scent that some say is reminiscent of apples. "</p>
<div class="control">
	<button class="btn">
	 <span class="price">49 $</span>
   <span class="buy">Buy Now</span>
 </button>
</div>
</div>
<div class="product-image">
	<img src="https://sc01.alicdn.com/kf/HTB1Cic9HFXXXXbZXpXXq6xXFXXX3/200006212/HTB1Cic9HFXXXXbZXpXXq6xXFXXX3.jpg" alt="Omar Dsoky"/>
</div>
</div>
        </div>
    )
}

export const ApartmenPage = () =>{
    const {request, loading} = useHttp();
    const [id,setId] = useState(null);
    const apartmenId = useParams().id;
    const navigate =  useNavigate();

    const getData = useCallback( async()=>{
        try
        {
            const fetched = await request('/api/'+apartmenId, 'GET', null,{});
            setId(fetched);
        }
        catch(e)
        {
            navigate('/');
        }
    },[request]);

    useEffect(()=>{
        getData();
    },[getData]);

    return(
        <div>
            {!loading && id && <ApartmenPageCart apartment={id}/>}
        </div>
    );
}
