import React,{useCallback, useEffect, useState} from "react";
import { useHttp } from "../hooks/http.hook";
import { useNavigate } from "react-router-dom";


export const MainPageCart = ({data, perPage, total}) => {
    const link = (id) =>{return "/apartmen/"+id;}
    const pageNumber = [];

    for(let i = 0; i <= Math.ceil(total/perPage); i++)
    {
        pageNumber.push(i);
    }

    return(
        <div>
            <nav>
                <div class="nav-wrapper red">
                    <a href = '/'><img class="brand-logo logou" src="https://cdn5.zp.ru/job/attaches/2016/07/99/b2/99b2dc5b6ce63ac1f1727bab91700532.png"/></a>
                </div>
            </nav>
            <div class="pad">
                <div class="row">
                    {data.map(apartmen => (
                        <div class="col s12 m6 l3">
                            <div class="row centre">
                                <div class="col s12 m7 bor">
                                    <a href = '#'>
                                    <div class="card">
                                        <div class="card-image">
                                            <img src="https://materializecss.com/images/sample-1.jpg"/>
                                            <span class="card-title">Card Title</span>
                                        </div>
                                        <div class="card-content">
                                            <p>I am a very simple card. I am good at containing small bits of information.
                                            I am convenient because I require little markup to use effectively.</p>
                                        </div>
                                    </div>
                                    </a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <ul class="pagination flexrow">
            {pageNumber.map(number => (
                <li class="waves-effect" key ={number}><a href="#!">{number}</a></li>
            ))}
            </ul>
        </div>
        
    )
}

export const MainPage = () =>{
    const {request, loading} = useHttp();
    const [data, setData] = useState(null);
    const navigate =  useNavigate();

    const getDataAll = useCallback( async()=>{
        try
        {
            const fetched = await request('/api/all', 'GET', null,{});
            setData(fetched);
        }
        catch(e){}
    },[request]);

    useEffect(()=>{
        getDataAll();
    },[getDataAll]);

    const [currentPage, setCurrentPage] = useState(1);
    const [perPage] = useState(10);

    const LastIndex = currentPage * perPage;
    const FirstIndex = LastIndex - perPage;
    const current = data.slice(FirstIndex,LastIndex);


    return(
        <div>
            {!loading && data && <MainPageCart 
                data={data}
                perPage = {perPage}
                total = {data.length}
            />}
        </div>
    );
}
